def on_on_overlap(sprite, otherSprite):
    if mySprite.overlaps_with(Ball2):
        mySprite.set_position(randint(10, 160), randint(10, 120))
        info.player1.change_score_by(1)
    else:
        if mySprite2.overlaps_with(Ball2):
            mySprite2.set_position(randint(10, 160), randint(10, 120))
            info.player2.change_score_by(1)
sprites.on_overlap(SpriteKind.player, SpriteKind.Ball, on_on_overlap)

