def on_forever():
    mySprite.set_stay_in_screen(True)
    controller.move_sprite(mySprite)
    mySprite2.set_stay_in_screen(True)
    controller.player2.move_sprite(mySprite2)
forever(on_forever)

def on_forever2():
    if info.score() == 10:
        game.splash("Player 1 Wins !!!!")
        game.reset()
    else:
        if info.player2.score() == 10:
            game.splash("Player 2 Wins !!!!")
            game.reset()
forever(on_forever2)

def on_update_interval():
    game.splash("Naranga is the best school ever !!! Press A button to continue")
game.on_update_interval(20000, on_update_interval)
